// Base64 encoded PDF data for email attachments
// This ensures 100% reliability on both localhost and Vercel

export const EMAIL_REPORT_ONE_PAGE_BASE64 = ``;

export const FULL_REPORT_SAMPLE_BASE64 = ``;

// Helper function to create PDF buffers
export const createPdfBuffers = () => {
  const emailReportOnePage = Buffer.from(EMAIL_REPORT_ONE_PAGE_BASE64, 'base64');
  const fullReportSample = Buffer.from(FULL_REPORT_SAMPLE_BASE64, 'base64');
  
  return {
    emailReportOnePage,
    fullReportSample
  };
};
